$stream = ""

foreach ($num in 5..$stream.Length) {
    $segment = $stream[$num-4]+$stream[$num-3]+$stream[$num-2]+$stream[$num-1]
    $result = 0
    foreach ($char in $segment.ToCharArray()) {
        if ($segment.IndexOf($char) -ne $segment.LastIndexOf($char)) {$result++}
    }
    if ($result -eq 0) {Write-Host "Part one:"$num; break}
}

foreach ($num in 15..$stream.Length) {
    $segment = $null
    foreach ($int in 14..1) {
        $segment += $stream[$num-$int]
    }
    $result = 0
    foreach ($char in $segment.ToCharArray()) {
        if ($segment.IndexOf($char) -ne $segment.LastIndexOf($char)) {$result++}
    }
    if ($result -eq 0) {Write-Host "Part two:"$num; break}
}
